import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) throw new Error("Could not find root element to mount to");

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import AudienceSplit from './components/AudienceSplit';
import HowItWorks from './components/HowItWorks';
import EducationSolutions from './components/EducationSolutions';
import ContentShowcase from './components/ContentShowcase';
import Ecosystem from './components/Ecosystem';
import Onboarding from './components/Onboarding';
import Pricing from './components/Pricing';
import FAQ from './components/FAQ';
import Footer from './components/Footer';
import Dashboard from './app/dashboard/Dashboard';

const App: React.FC = () => {
  const [currentHash, setCurrentHash] = useState(window.location.hash);

  useEffect(() => {
    const handleHashChange = () => {
      setCurrentHash(window.location.hash);
      window.scrollTo(0, 0); // Scroll to top on navigation
    };

    window.addEventListener('hashchange', handleHashChange);
    return () => window.removeEventListener('hashchange', handleHashChange);
  }, []);

  // Simple Router
  if (currentHash === '#dashboard' || currentHash === '#app') {
    return <Dashboard />;
  }

  return (
    <div className="min-h-screen bg-[#FBFBFD]">
      <Navbar />

      <main>
        <section id="hero">
          <Hero />
        </section>

        {/* 1. SPEED: "Launch in 48h" */}
        <section id="fast-launch">
          <Onboarding />
        </section>

        {/* 2. PROCESS: "How LIA Sells" (WhatsApp Mockups) */}
        <section id="process">
          <HowItWorks />
        </section>

        {/* 3. MAGIC: "Content Studio" */}
        <section id="content">
          <ContentShowcase />
        </section>

        {/* 4. ECOSYSTEM: "All-in-One" */}
        <section id="features">
          <Ecosystem />
        </section>

        {/* 5. EXPERTISE: "We know Education" */}
        <section id="solutions">
          <EducationSolutions />
        </section>

        {/* 6. SCALE: "Designed for You" (Moved DOWN) */}
        <section id="audience" className="scroll-mt-20">
          <AudienceSplit />
        </section>

        <section id="precios">
          <Pricing />
        </section>

        <section id="faq">
          <FAQ />
        </section>
      </main>

      <Footer />
    </div>
  );
};

export default App;

:root {
    /* Branding - iOS Style */
    --color-primary: #007AFF;
    /* iOS Blue */
    --color-secondary: #FF9500;
    /* iOS Orange */

    /* Alias for compatibility */
    --brand: #007AFF;
}

/* Reverted global scale */

body {
    margin: 0;
    padding: 0;
    font-family: 'Inter', sans-serif;
}

// Fix: Added React import to resolve "Cannot find namespace 'React'" error for types like React.ReactNode.
import React from 'react';

export interface FeatureCardProps {
  icon: React.ReactNode;
  title: string;
  description: string;
  tag?: string;
}

export interface PricingPlanProps {
  name: string;
  price: string;
  subtitle: string;
  features: string[];
  limitations?: string;
  recommended?: boolean;
}

export interface ComparisonRowProps {
  label: string;
  traditional: string;
  software: string;
  lia: string;
  liaHighlight?: boolean;
}
